import React, { useState } from "react";

const App = () => {
  const purple = "#8e44ad";
  const [bg, setBg] = useState(purple);
  const [name, setName] = useState('click me ');

    
  const bgChange = () => {
    // console.log("clicked");
    let newBg = '#34495e';
    setBg(newBg);
    setName("Ouch!! ðŸº")
  };

  const bgBack = () => {
    setBg(purple);
    setName()
  } 
  return (
    <>
      <div style = {{ backgroundColor: bg }}>
        <button onClick = {bgChange} onDoubleClick = {bgBack}> {name} </button>
      </div>
    </>
  );
};

export default App;
