import React, { useState } from "react";
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
const CreateNote = () => {
    const [note, setNote] =  useState({
        title: "",
        content: "",
    })

    const InputEvent = () => {
        setNote((prev))
    } 
  return (
    <>
        <div className="main_note">
            <form>
                <input type="text" value={note.title} onChange={InputEvent} placeholder="Title" autoComplete="off"/>
                <textarea rows="" column="" value={note.content} onChange={InputEvent} placeholder="Write a note..."/>
                <Button>
                    <AddIcon className="plus_sign"/>
                </Button>
            </form>
        </div>
    </>
  );
};

export default CreateNote;
